mapper.select:
select 
count(*) as totalPageCount
 from orderinfo where m_id = #{id}

mapper.insert:
insert into etc( etc_code, o_code, etc_type_code, etc_content, etc_date
,etc_pname, etc_spname, etc_ppath, etc_psize
) values( etc_code_sqn.nextval, #{o_code}, #{etc_type_code}, #{etc_content}, to_char(sysdate,'yyyy-mm-dd hh24:mi:ss')
,#{etc_pname}, #{etc_spname}, #{etc_ppath}, #{etc_psize}
)
